mapper.select:
SELECT SDB.SIDB_DOC_NUM, SDB.SIDB_DOC_WRITEDT, SDB.SIDB_DOC_TITLE, SDB.SIDB_DOC_CONTENT, SDB.SICA_CD, SDB.SIDB_DOC_EXPIREDT, SDB.SIDB_DOC_FLAG, SDB.SIDB_DOC_APPRDT, SDB.SIDB_DOC_STAT, SDB.EMPL_REF, SDB.EMPL_DEPT_CD, SDB.SIDB_DOC_JSON,SDB.EMPL_ID , E.EMPL_NAME , APPR_ID, e2.EMPL_NAME as APPR_NAME, CC2.COCO_NAME as APPR_RANK , CC.COCO_NAME as WRITER_DT, APPR_SIGN , APPR_REPLY, APPR_FLAG, APPR_DT FROM SIGN_DOC_BOX sdb JOIN EMPLOYEE e ON sdb.EMPL_ID = e.EMPL_ID
CROSS JOIN JSON_TABLE(SDB.SIDB_DOC_JSON, '$[*]' COLUMNS ( APPR_ID PATH '$.APPR_ID', APPR_DEPTH PATH '$.APPR_DEPTH', APPR_SIGN CLOB PATH '$.APPR_SIGN', APPR_DT PATH '$.APPR_DT', APPR_FLAG PATH '$.APPR_FLAG', APPR_REPLY PATH '$.APPR_REPLY' )) j
LEFT JOIN EMPLOYEE e2 ON j.APPR_ID = e2.EMPL_ID LEFT JOIN COMMON_CODE cc ON E.EMPL_DEPT_CD = CC.COCO_CD LEFT JOIN COMMON_CODE cc2 ON E2.EMPL_RANK_CD = CC2.COCO_CD WHERE SDB.SIDB_DOC_NUM = #{sidb_doc_num}
