mapper.insert:
INSERT INTO SIGN_DOC_BOX (SIDB_DOC_NUM, EMPL_ID, SIDB_DOC_TITLE, SIDB_DOC_CONTENT, SICA_CD, SIDB_DOC_EXPIREDT, SIDT_TEMP_CD, SIDB_DOC_BE, SIDB_DOC_END, SIDB_CURR_ID, SIDB_DOC_JSON) VALUES ((SELECT NVL(MAX(SIDB_DOC_NUM) + 1, 24000001) FROM SIGN_DOC_BOX), #{empl_id}, #{sidb_doc_title}, #{sidb_doc_content}, #{sica_cd}, #{sidb_doc_expiredt}, #{sidt_temp_cd}, #{sidb_doc_be}, #{sidb_doc_end}, #{sidb_curr_id}, '[
{ "APPR_ID":"${json.appr_id}", "APPR_DEPTH":"${json.appr_depth}", "APPR_SIGN":"", "APPR_DT":"", "APPR_FLAG":"0", "APPR_REPLY":"" }
]')

INSERT INTO SIGN_TEMP_BOX (SITB_DOC_NUM, EMPL_ID, SITB_DOC_TITLE, SITB_DOC_CONTENT, SICA_CD, SITB_DOC_EXPIREDT, SIDT_TEMP_CD, SIDB_CURR_ID, SITB_DOC_JSON) VALUES ((SELECT NVL(MAX(SITB_DOC_NUM) + 1, 24000001) FROM SIGN_TEMP_BOX), #{empl_id}, #{sitb_doc_title}, #{sitb_doc_content}, #{sica_cd}, #{sitb_doc_expiredt}, #{sidt_temp_cd}, #{sidb_curr_id}, '[
{ "APPR_ID":"${json.appr_id}", "APPR_DEPTH":"${json.appr_depth}", "APPR_SIGN":"", "APPR_DT":"", "APPR_FLAG":"0", "APPR_REPLY":"" }
]')

mapper.update:
UPDATE SIGN_DOC_BOX SET SIDB_DOC_TITLE = #{sidb_doc_title}, SIDB_DOC_CONTENT = #{sidb_doc_content}, SICA_CD = #{sica_cd}, SIDB_DOC_ALFLAG = #{sidb_doc_alflag}, SIDB_DOC_MODIFYDT = CURRENT_DATE, SIDT_TEMP_CD = #{sidt_temp_cd}, SIDB_DOC_BE = #{sidb_doc_be}, SIDB_DOC_END = #{sidb_doc_end}, EMPL_REF = #{empl_ref}, EMPL_DEPT_CD = #{empl_dept_cd}, SIDB_DOC_JSON = '[
{ "APPR_ID":"${json.appr_id}", "APPR_DEPTH":"${json.appr_depth}", "APPR_SIGN":"", "APPR_DT":"", "APPR_FLAG":"", "APPR_REPLY":"" }
]' WHERE SIDB_DOC_NUM = ${sidb_doc_num}

mapper.delete:
UPDATE SIGN_DOC_BOX
SET SIDB_DOC_DELFLAG = 'N'
WHERE SIDB_DOC_NUM = #{sidb_doc_num}
