mapper.select:
SELECT 
	      COCO_CD, COCO_NAME 
	FROM    COMMON_CODE
	WHERE    COCO_CD = #{coco_cd}
	AND      SUBSTR(COCO_CD, 1, 2) = #{role}
	AND      COCO_PARENT_ID IS NOT NULL

SELECT 
	      COCO_CD, COCO_NAME 
	FROM    COMMON_CODE
	WHERE    COCO_NAME = #{coco_name}
	AND      SUBSTR(COCO_CD, 1, 2) = #{role}
	AND      COCO_PARENT_ID IS NOT NULL
